hudson.tasks.Shell.command:
getIdForCategoryName() { curl -s "${EDM_HOST}/category/name/$1" | sed 's/.*"id":"\(.*\)","name".*/\1/g' }
addColorOnCategory() { echo "{\"id\":\"$1\",\"name\":\"$2\",\"color\":\"$3\",\"backgroundColor\":\"$4\"}" }
postNewCategory() { curl -s -XPOST "${EDM_HOST}/category" --data "$1" -H "Content-Type: application/json" }
#############
categoryName="Factures" categoryId=$(getIdForCategoryName "${categoryName}") newCategory=$(addColorOnCategory "${categoryId}" "${categoryName}" "#FFF" "#ff5722") postNewCategory "${newCategory}"
categoryName="Administration" categoryId=$(getIdForCategoryName "${categoryName}") newCategory=$(addColorOnCategory "${categoryId}" "${categoryName}" "#FFF" "#f44336") postNewCategory "${newCategory}"
categoryName="Banque" categoryId=$(getIdForCategoryName "${categoryName}") newCategory=$(addColorOnCategory "${categoryId}" "${categoryName}" "#FFF" "#03a9f4") postNewCategory "${newCategory}"
