hudson.tasks.Shell.command:
ELASTIC_HOST=http://127.0.0.1:9200


# where mapping files are located 
cd /home/xavier/work/paperless-documents-search/edm-webapp/src/main/resources/mapping

curl -XDELETE "${ELASTIC_HOST}/${NEW_INDEX_NAME}"

curl -XPUT "${ELASTIC_HOST}/${NEW_INDEX_NAME}" -d "@./documents.json"
curl -XPUT "${ELASTIC_HOST}/${NEW_INDEX_NAME}/_mapping/category" -d "@./documents/category.json"
curl -XPUT "${ELASTIC_HOST}/${NEW_INDEX_NAME}/_mapping/source" -d "@./documents/source.json"
curl -XPUT "${ELASTIC_HOST}/${NEW_INDEX_NAME}/_mapping/document_file" -d "@./documents/document_file.json"

# here you shoumd re-index data before swithing index


if [ -z "${OLD_INDEX_NAME}" ]; then
curl -XPOST "${ELASTIC_HOST}/_aliases" -d '
{
    "actions" : [
        { "add" : { "index" : "'${NEW_INDEX_NAME}'", "alias" : "documents" } }
    ]
}'
else
curl -XPOST "${ELASTIC_HOST}/_aliases" -d '
{
    "actions" : [
        { "remove" : { "index" : "'${OLD_INDEX_NAME}'", "alias" : "documents" } },
        { "add" : { "index" : "'${NEW_INDEX_NAME}'", "alias" : "documents" } }
    ]
}'
fi
