definition.script:
pipeline {
    agent any

    environment {
		directory_to_scan = '/home/xavier/documents/to_tidy/'
        scripts_directory = "${env.EDM_CRAWLERS_BASE_DIR}/auto-tidy-docs-pdf"
    }

    stages {
        stage('Detect file') {
            steps {
                script {
                    file_name = sh (returnStdout: true, script:"cd \"${env.directory_to_scan}\" && find . -type f -iname '*.pdf' ! -iname '*locked*' | head -1").trim()
                    echo "file_name : ${file_name}"

                    if ("${file_name}" == "") {
                        currentBuild.result = 'ABORTED'
                        error("No file to sort found, won't continue")
                    }
                }
            }
        }
        stage('Lock file'){
            steps {
                echo "Dealing with file : ${file_name}"
                script {
                    locked_file_name = sh (returnStdout: true, script: "echo ${file_name} | sed 's/\\(.*\\)\\(.pdf\\)/\\1.locked\\2/g'").trim()
                    echo "locking file (name=${locked_file_name})"
                    sh "cd \"${env.directory_to_scan}\" && mv \"${file_name}\" \"${locked_file_name}\""
                }
            }
        }
        stage('Convert file to image'){
            steps {
                script {
                    tiff_file_name = "${locked_file_name}.tiff"
                    echo "tiff file (name=${tiff_file_name})"
                    sh "cd \"${env.directory_to_scan}\" && convert -depth 8 -density 300 \"${locked_file_name}\" \"${tiff_file_name}\""
                }
            }
        }
        stage('Extract text from image') {
            steps {
                script {
                    text_file_name = "${locked_file_name}.txt"
                    echo "text (name=${text_file_name})"
                    sh "cd \"${env.directory_to_scan}\" && tesseract \"${tiff_file_name}\" -l fra - | sed '/^[[:space:]]*\$/d' > \"${text_file_name}\""
                    sh "cd \"${env.directory_to_scan}\" && cat \"${text_file_name}\""
                }
            }
        }
        stage('Decide where I should move the document') {
            steps {
                script {
                    sh "cd \"${env.scripts_directory}\" && ./magic_sorter.sh \"${directory_to_scan}/${text_file_name}\""
                    destination_file = sh (returnStdout: true, script:"cd \"${env.directory_to_scan}\" && cat \"${text_file_name}.dest\"").trim()
                    echo "destination_file (name=${destination_file})"
                }
            }
        }
        stage('Remove working files') {
            steps {
                sh "cd \"${env.directory_to_scan}\" && rm -v \"${locked_file_name}\".*"
            }
        }
        stage('Waiting for an human to confirm my choice') {
            steps {
                script {
                    def userInput = input(
                        id: 'inputConfirmMove',
                        message: "Move the file to ${destination_file} ?"
                    )
                }
            }
        }
        stage('Moving !') {
            steps {
                script {
                    destinationFileNameEscaped=sh (returnStdout: true, script:"echo \"${destination_file}\" | sed 's/ /\\ /g'").trim()
                    destinationDir = sh (returnStdout: true, script:"dirname \"${destinationFileNameEscaped}\"").trim()
                    destinationFileName = sh (returnStdout: true, script:"basename \"${destinationFileNameEscaped}\"").trim()
                    echo "destinationDir (dir=${destinationDir})"
                    echo "destinationFileName (name=${destinationFileName})"
                    sh "mkdir -p \"${destinationDir}\""
                    sh "cd \"${env.directory_to_scan}\" && mv -v \"${locked_file_name}\" \"${destinationFileName}\""
                    sh "cd \"${env.directory_to_scan}\" && mv -v \"${destinationFileName}\" \"${destinationDir}\""
                }
            }
        }
    }
}
